CREATE DATABASE QuanLyTrungTamEco;
USE QuanLyTrungTamEco

CREATE TABLE CoSo (
	MaCS varchar(10) PRIMARY KEY NOT NULL,
	TenCS nvarchar(100) NOT NULL,
	DiaChi nvarchar(200) NOT NULL,
	MaQL varchar(10),
)

UPDATE CoSo SET MaQL = 'QL001' WHERE MaCS = 'CS0001';
UPDATE CoSo SET MaQL = 'QL002' WHERE MaCS = 'CS0002';
UPDATE CoSo SET MaQL = 'QL003' WHERE MaCS = 'CS0003';
UPDATE CoSo SET MaQL = 'QL004' WHERE MaCS = 'CS0004';
UPDATE CoSo SET MaQL = 'QL005' WHERE MaCS = 'CS0005';
UPDATE CoSo SET MaQL = 'QL006' WHERE MaCS = 'CS0006';


CREATE TABLE PhongHoc (
	MaPH varchar(10) PRIMARY KEY NOT NULL,
	MaCS varchar(10) NOT NULL,
	TenPH nvarchar(20) NOT NULL,
	CONSTRAINT fk_PhongHoc_Coso FOREIGN KEY(MaCS) REFERENCES CoSo(MaCS)
)

ALTER TABLE PhongHoc ALTER COLUMN TenPH nvarchar(255)

CREATE TABLE NhanVien (
	MaNV varchar(10) PRIMARY KEY NOT NULL,
	MaCS varchar(10) NOT NULL,
	HoTen nvarchar(50) NOT NULL,
	NgaySinh date,
	SĐT varchar(10) NOT NULL,
	ChucVu nvarchar(50) NOT NULL,
	TrinhDo nvarchar(50),
	Luong int NOT NULL,
	CONSTRAINT fk_NhanVien_Coso FOREIGN KEY(MaCS) REFERENCES CoSo(MaCS)
)

CREATE TABLE HocVien (
	MaHV varchar(10) PRIMARY KEY NOT NULL,
	HoTen nvarchar(50) NOT NULL,
	GioiTinh varchar(10) NOT NULL,
	NgaySinh date,
	QueQuan nvarchar(50) NOT NULL,
	SĐT varchar(10) NOT NULL,
	TrangThai nvarchar(100),
)


CREATE TABLE KhoaHoc (
	MaKH varchar(10) PRIMARY KEY NOT NULL,
	TenKH nvarchar(50) NOT NULL,
	SoBuoi int,
	HocPhi int NOT NULL,
)

CREATE TABLE GiaoTrinh (
	MaGT varchar(10) PRIMARY KEY NOT NULL,
	TieuDe nvarchar(30) NOT NULL,
	MaKH varchar(10) NOT NULL,
	CONSTRAINT fk_GiaoTrinh_KhoaHoc FOREIGN KEY(MaKH) REFERENCES KhoaHoc(MaKH)
)


CREATE TABLE LopHoc (
	MaLH varchar(10) PRIMARY KEY NOT NULL,
	MaKH varchar(10) NOT NULL,
	MaNV varchar(10) NOT NULL,
	TenLH nvarchar(50) NOT NULL,
	CONSTRAINT fk_LopHoc_KhoaHoc FOREIGN KEY(MaKH) REFERENCES KhoaHoc(MaKH),
	CONSTRAINT fk_GiaoVien_LopHoc FOREIGN KEY(MaNV) REFERENCES NhanVien(MaNV)
)



CREATE TABLE TietHoc (
	MaTH varchar(10) PRIMARY KEY NOT NULL,
	MaLH varchar(10) NOT NULL,
	MaPH varchar(10) NOT NULL,
	MaTG varchar(10) NOT NULL,
	ThoiGian datetime,
	TrangThai varchar(20),
	CONSTRAINT fk_TietHoc_LopHoc FOREIGN KEY(MaLH) REFERENCES LopHoc(MaLH),
	CONSTRAINT fk_TietHoc_PhongHoc FOREIGN KEY(MaPH) REFERENCES PhongHoc(MaPH),
	CONSTRAINT fk_TietHoc_NhanVien FOREIGN KEY(MaTG) REFERENCES NhanVien(MaNV)
) 

CREATE TABLE HopDong (
	MaHD varchar(10) PRIMARY KEY NOT NULL,
	MaHV varchar(10) NOT NULL,
	MaNV varchar(10) NOT NULL,
	HinhThucThanhToan nvarchar(50),
	ThoiGian datetime,
	CONSTRAINT fk_HopDong_HocVien FOREIGN KEY(MaHV) REFERENCES HocVien(MaHV),
	CONSTRAINT fk_HopDong_NhanVien FOREIGN KEY(MaNV) REFERENCES NhanVien(MaNV),
)

CREATE TABLE ChiTietHD (
	MaHD varchar(10) NOT NULL,
	MaLH varchar(10) NOT NULL,
	SoTien int,
	KhuyenMai nvarchar(100),
	CONSTRAINT fk_ChiTietHD_LopHoc FOREIGN KEY(MaLH) REFERENCES LopHoc(MaLH),
	CONSTRAINT fk_ChiTietHD_HopDong FOREIGN KEY(MaHD) REFERENCES HopDong(MaHD),
)


